function createToast(type, message, timeout) {
  const div = document.createElement("div"),
    innerDiv = document.createElement("div"),
    contentDiv = document.createElement("div"),
    contentSpan = document.createElement("span"),
    contentMessage = document.createElement("span"),
    closeButton = document.createElement("button"),
    closeSvg = document.createElementNS("http://www.w3.org/2000/svg", "svg"),
    closePath = document.createElementNS("http://www.w3.org/2000/svg", "path");
  return (
    (div.className = "alert shadow-lg h-auto w-fit"),
    (innerDiv.className = "gap-x-6"),
    (contentDiv.className = "flex justify-center items-center gap-x-3 text-sm"),
    (contentSpan.innerHTML =
      "error" == type
        ? "❌"
        : "success" == type
        ? "✅"
        : "warning" == type
        ? "&#9888;&#65039;"
        : "❓"),
    (contentMessage.innerHTML = message),
    (closeButton.className = "btn btn-xs btn-square btn-outline"),
    (closeButton.id = "toast_close"),
    closeSvg.setAttribute("xmlns", "http://www.w3.org/2000/svg"),
    closeSvg.setAttribute("class", "h-4 w-4"),
    closeSvg.setAttribute("fill", "none"),
    closeSvg.setAttribute("viewBox", "0 0 24 24"),
    closeSvg.setAttribute("stroke", "currentColor"),
    closePath.setAttribute("stroke-linecap", "round"),
    closePath.setAttribute("stroke-linejoin", "round"),
    closePath.setAttribute("stroke-width", "2"),
    closePath.setAttribute("d", "M6 18L18 6M6 6l12 12"),
    closeSvg.appendChild(closePath),
    closeButton.appendChild(closeSvg),
    contentDiv.appendChild(contentSpan),
    contentDiv.appendChild(contentMessage),
    innerDiv.appendChild(contentDiv),
    innerDiv.appendChild(closeButton),
    div.appendChild(innerDiv),
    closeButton.addEventListener("click", function () {
      div.classList.add("hidden");
    }),
    setTimeout(() => {
      div.classList.add("hidden");
    }, timeout),
    div
  );
}
!(function (window) {
  window.showToast = function (type, message, timeout = 15e3) {
    (toast_body = document.getElementById("toast_body")),
      toast_body.appendChild(createToast(type, message, timeout));
  };
})(window);
