function getInfoFromAppFunc(app_info_label) {
    getInfoFromApp(
        function(data) {
            app_running = data['running'];
            app_running = app_running ? 'üöÄ running' : 'üíÄ stoped';
            app_proceed = data['proceed_time'];
            app_stop_time = '';
            app_stop_reason = '';
            if(app_proceed) {
                let seconds = parseFloat(app_proceed);
            
                let years = Math.floor(seconds / (365*24*60*60));
                seconds -= years*365*24*60*60;
                let days = Math.floor(seconds / (24*60*60));
                seconds -= days*24*60*60;
                let hours = Math.floor(seconds / (60*60));
                seconds -= hours*60*60;
                let minutes = Math.floor(seconds / 60);
                seconds = Math.floor(seconds - minutes*60);
            
                let result = "";
            
                if (years > 0)
                    result += `${years}y: `;
                if (days > 0)
                    result += `${days}d: `;
                if (hours > 0)
                    result += `${hours}h: `;
                result += `${minutes}min`;
            
                app_proceed = result;
            }
            if (data['stop_time'])
                app_stop_time = 'üõë ' + data['stop_time'];
            if (data['stop_reason'])
                app_stop_reason = '‚ùå ' + data['stop_reason'];
            app_info_label.innerHTML = `<span class="run-info text-success">${app_running}</span> <span class="proceed-info text-info">üïí ${app_proceed}</span> <span class="stop-time-info text-warning">${app_stop_time}</span> <span class="stop-reason-info text-error">${app_stop_reason}</span>`
        },
        function(error) {
            app_info_label.innerHTML = `<span class="stop-reason-info text-error">${error}</span>`
        }
    );
}

