window.verifyHcaptcha = function (token, successCallback, errorCallback) {
  const url = '/verify-hcaptcha';
  const headers = { };
  const formData = new FormData();
  formData.append('hcaptcha-token', token);
  
  requestsPOST(
    url, 
    headers,
    formData,
    function(error, data, timeDiff) {
      if (error) {
          errorCallback();
          showToast('error', error);
      } else {
          if(data.success){
            successCallback();
            showToast('success', timeDiff, 700);
          } else {
            errorCallback();
            showToast('error', data.error);
          }
      }
    },
    function(progress) {/* NOTE: Fetch API doesn't support progress updates as of September 2021. */},
    true // it's a Fetch API
  );
};

