!(function (window) {
  window.showLottie = function (id) {
    document.querySelectorAll(`#${id}`).forEach(item => {
        lottie.loadAnimation({
          container: item,
          renderer: "svg",
          loop: !0,
          autoplay: !0,
          path: `../../static/img/lotties/${id}.json`,
          rendererSettings: {
            preserveAspectRatio: "xMidYMid meet",
            progressiveLoad: !1,
            scaleMode: "noScale",
            hideOnTransparent: !0,
          },
        });
    })
  };
  window.removeLottie = function (id) {
      document.querySelectorAll(`#${id}`).forEach(item => {
        if(item) {
          item.innerHTML= '';
        }
      })
  };
})(window);
